import {useState, useEffect} from 'react';
import {TETROMINOS} from '../tetrominos';
import { checkCollision, STAGE_HEIGHT } from '../gameHelpers';


export const useGhost = (player) => {
    const [ghost, setGhost] = useState({
        pos: {x: 0, y: 20},
        tetromino: player.tetromino,
        collided: false,
    });

    const rotate = (matrix, dir) => {
        // just transpose the matrix ig
        const rotatedTetro = matrix.map((_, index) => matrix.map(col => col[index]),);

        //reverse rows to get rotated matrix
        if (dir > 0) {
            return rotatedTetro.map(row => row.reverse());
        } else{
            return rotatedTetro.reverse();
        }
    }

    const ghostRotate = (stage, dir) => {
        //make deep clone of ghost to prevent mutations
        const clonedGhost = JSON.parse(JSON.stringify(ghost)); 
        clonedGhost.tetromino = rotate(clonedGhost.tetromino, dir);

        const pos = clonedGhost.pos.y;
        rotate(clonedGhost.tetromino, -dir);

        console.log("made it here");

        let yVal = 0;
        while (checkCollision(clonedGhost, stage, {x: 0, y: yVal})){
            yVal -= 1;
        }
        clonedGhost.pos.y += yVal;

        setGhost(ghostRotate);

    }

    const updateGhostPos = ({x, y, collided}, stage) => {
        let h = 0;
        while (checkCollision(ghost, stage, {x: 0, y: 0})){
            h -= 1;
        }
        setGhost(prev => ({
            ...prev,
            pos: {x: (prev.pos.x += x), y: (h)},
            collided,
        }))
    }

    const resetGhost = (player, y) =>{
        console.log(player);
        setGhost({
            pos: {x: player.pos.x, y: y},
            tetromino: player.tetromino,
            collided: player.pos.collided,
        })
    }

    return [ghost, updateGhostPos, resetGhost, ghostRotate]; 
}
