import {useState, useCallback} from 'react';
import { checkCollision, STAGE_WIDTH } from '../gameHelpers';

import {TETROMINOS, randomTetromino} from '../tetrominos';

export const usePlayer = () => {
    const [player, setPlayer] = useState({
        pos: {x: 0, y: 0},
        tetromino: TETROMINOS[0].shape,
        collided: false,
    });

    const rotate = (matrix, dir) => {
        // just transpose the matrix ig
        const rotatedTetro = matrix.map((_, index) => matrix.map(col => col[index]),);

        //reverse rows to get rotated matrix
        if (dir > 0) {
            return rotatedTetro.map(row => row.reverse());
        } else{
            return rotatedTetro.reverse();
        }
    }

    const playerRotate = (stage, dir) => {
        //make deep clone of player to prevent mutations
        const clonedPlayer = JSON.parse(JSON.stringify(player)); 
        clonedPlayer.tetromino = rotate(clonedPlayer.tetromino, dir);

        const pos = clonedPlayer.pos.x;
        let offset = 1;
        while (checkCollision(clonedPlayer, stage, {x: 0, y: 0})){
            clonedPlayer.pos.x += offset;
            offset = -(offset + (offset > 0 ? 1 : -1));
            if (offset > clonedPlayer.tetromino[0].length){
                rotate(clonedPlayer.tetromino, -dir);
                clonedPlayer.pos.x = pos;
                return;
            }
        }

        setPlayer(clonedPlayer);

    }

    const updatePlayerPos = ({x, y, collided}) => {
        setPlayer(prev => ({
            ...prev,
            pos: {x: (prev.pos.x += x), y: (prev.pos.y += y)},
            collided,
        }))
    }

    const resetPlayer = useCallback(() =>{
        setPlayer({
            pos: {x: STAGE_WIDTH/2-2, y: 0},
            tetromino: randomTetromino().shape,
            collided: false,
        })
    }, [])

    const swapPlayer = (holdingPiece) => {
        setPlayer({
            pos: {x: STAGE_WIDTH/2-2, y: 0},
            tetromino: holdingPiece.tetromino,
            collided: false,
        })
    }

    return [player, updatePlayerPos, resetPlayer, playerRotate, setPlayer];

}
