import {useState, useEffect} from 'react';
import {createHold} from '../gameHelpers';

export const useHold = (player, resetPlayer) => {
    const[hold, setHold] = useState(createHold(player.tetromino.shape.length, player.tetromino.shape.length));

    useEffect(() => {
        const updateHold = prevHold => {
            //Flush stage - reset and rebuild
            const newHold = prevHold.map(row => 
                row.map(cell => (cell[1] === [0, 'clear']))
            );

            //Draw tetromino
            player.tetromino.forEach((row, y) => {
                row.forEach((value, x) => {
                    if (value !== 0){
                        newHold[y][x] = [value, 'clear']; 
                    }
                });
            });

            return newHold;
        };

        setHold(prev => updateHold(prev))
    }, [player]);

    return [hold, setHold]; 
}
